import React from 'react'
import './testimonials.css'
import AVTR1 from '../../assets/avatar1.jpg'
import AVTR2 from '../../assets/avatar2.jpg'
import AVTR3 from '../../assets/avatar3.jpg'
import AVTR4 from '../../assets/avatar4.jpg'

const data = [
  {
    avatar: AVTR1,
    name: 'Tina Snow',
    review: 'Lorem ipsum, dolor sit amet consectetur adipisicing elit. Beatae voluptas sequi libero veritatis cupiditate ab pariatur esse, nam maxime consequuntur ut ipsam, ipsa ratione, voluptatem cumque ex quaerat doloribus unde.'
  },
  {
    avatar: AVTR2,
    name: 'Shutta Wale',
    review: 'Lorem ipsum, dolor sit amet consectetur adipisicing elit. Beatae voluptas sequi libero veritatis cupiditate ab pariatur esse, nam maxime consequuntur ut ipsam, ipsa ratione, voluptatem cumque ex quaerat doloribus unde.'
  },
  {
    avatar: AVTR3,
    name: 'Kwame Despite',
    review: 'Lorem ipsum, dolor sit amet consectetur adipisicing elit. Beatae voluptas sequi libero veritatis cupiditate ab pariatur esse, nam maxime consequuntur ut ipsam, ipsa ratione, voluptatem cumque ex quaerat doloribus unde.'
  },
  {
    avatar: AVTR4,
    name: 'Amana McBrown',
    review: 'Lorem ipsum, dolor sit amet consectetur adipisicing elit. Beatae voluptas sequi libero veritatis cupiditate ab pariatur esse, nam maxime consequuntur ut ipsam, ipsa ratione, voluptatem cumque ex quaerat doloribus unde.'
  }
]

const Testimonials = () => {
  return (
    <section id='testimonials'>
      <h5>Client's Review</h5>
      <h2>Testimonials</h2>

      <div className="container testimonials__container">
        {
          data.map(({avatar, name, review}, index) => {
            return (
              <article key={index} className="testimonial">
              <div className="client__avatar">
                <img src={avatar}/>
              </div>
              <h5 className='client__name'>{name}</h5>
              <small className='client__review'>{review}</small>
             </article>
            )
          })
        }
      </div>
    </section>
  )
}

export default Testimonials
